```tsx
// MOBILE-OPTIMIZED COMPONENTS
const MobileOptimizedButton = ({ children, onClick, variant = "primary" }) => { const baseClasses = ` min-h-[48px] px-6 py-3 rounded-lg font-medium touch-manipulation select-none transition-all duration-150 ease-out active:scale-98 active:opacity-90 focus:outline-none focus:ring-4 focus:ring-blue-200 `; const variantClasses = { primary: 'bg-blue-600 text-white hover:bg-blue-700', secondary: 'bg-white text-blue-600 border-2 border-blue-600 hover:bg-blue-50', tertiary: 'bg-gray-100 text-gray-700 hover:bg-gray-200' }; return ( <button className={`${baseClasses} ${variantClasses[variant]}`} onClick={onClick} type="button" > {children} </button> );
}; // MOBILE-FRIENDLY FORM INPUT
const MobileInput = ({ label, error,...props }) => { return ( <div className="space-y-2"> <label className="block text-sm font-medium text-gray-700"> {label} </label> <input className={` w-full min-h-[44px] px-4 py-3 text-base border-2 rounded-lg touch-manipulation ${error? 'border-red-500 focus:border-red-600 focus:ring-red-200': 'border-gray-300 focus:border-blue-500 focus:ring-blue-200' } focus:outline-none focus:ring-4 transition-colors duration-200 `} {...props} /> {error && ( <p className="text-sm text-red-600 flex items-center gap-1"> <span></span> {error} </p> )} </div> );
}; // MOBILE BOTTOM NAVIGATION
const MobileBottomNav = ({ activeTab, onTabChange }) => { const tabs = [ { id: 'trip', label: 'Trip', icon: MapIcon }, { id: 'list', label: 'Packing', icon: ClipboardIcon }, { id: 'suggestions', label: 'AI Help', icon: LightBulbIcon } ]; return ( <nav className="fixed bottom-0 left-0 right-0 bg-white border-t border-gray-200 pb-safe z-50"> <div className="flex justify-around"> {tabs.map(tab => ( <button key={tab.id} onClick={() => onTabChange(tab.id)} className={` flex flex-col items-center min-h-[44px] px-3 py-2 touch-manipulation select-none transition-colors duration-200 ${activeTab === tab.id? 'text-blue-600': 'text-gray-500 hover:text-gray-700' } `} > <tab.icon className="h-6 w-6 mb-1" /> <span className="text-xs font-medium">{tab.label}</span> </button> ))} </div> </nav> );
};
```

```tsx
// MOBILE PERFORMANCE PATTERNS
const useMobileOptimization = () => { // Debounce touch events for better performance const debouncedTouch = useCallback( debounce((callback) => callback(), 16), // 60fps [ ] ); // Optimize scroll performance const useVirtualizedList = (items, itemHeight = 60) => { const [visibleItems, setVisibleItems] = useState([ ]); const containerRef = useRef(null); useEffect(() => { const container = containerRef.current; if (!container) return; const handleScroll = () => { const scrollTop = container.scrollTop; const containerHeight = container.clientHeight; const startIndex = Math.floor(scrollTop / itemHeight); const endIndex = Math.min( startIndex + Math.ceil(containerHeight / itemHeight) + 1, items.length ); setVisibleItems(items.slice(startIndex, endIndex)); }; container.addEventListener('scroll', handleScroll, { passive: true }); handleScroll(); // Initial render return () => container.removeEventListener('scroll', handleScroll); }, [items, itemHeight]); return { visibleItems, containerRef }; }; return { debouncedTouch, useVirtualizedList };
}; // MOBILE IMAGE OPTIMIZATION
const MobileOptimizedImage = ({ src, alt, className }) => { return ( <img src={src} alt={alt} className={className} loading="lazy" decoding="async" style={{ maxWidth: '100%', height: 'auto', objectFit: 'cover' }} /> );
};
